import * as types from './types';


export const registerUser = () => ({
  type: types.GET_SERVER_PROVIDER,
  payload: {
    path: `/serverproviders/all/details`,
    method: 'GET',
  },
  meta: {
    api: true,
    successMessage: 'Server provider fetch successfully',
    errorMessage: "Server  provider failed to fetch",
  },
});


export const saveAuthUser = (payload) => ({
  type: types.SAVE_USER_INFO,
  payload: {
    path: `/`,
    method: 'GET',
  },
  meta: {
    api: true,
    successMessage: 'User registered successfully',
    errorMessage: "User registration failed",
  },
});


export const clearState = () => ({
  type: 'RESET_APP',
  meta: {
    api: false,
    successMessage: 'Successfully State Clear',
    errorMessage: "State not clear",
  },
});
