import produce from 'immer';
import * as types from './types';

const authInfoState = {
    msg: '',
}

export const authInfo = (state = authInfoState, action) => {
    const { type, payload, meta, errorMessage } = action;
    return produce(state, (draft) => {
        switch (type) {
            case types.SAVE_USER_INFO:
                console.log('saving user')
                // draft.userInfo = payload.idTokenParsed
                // draft.userInfo.token = payload.token
                break;
            case types.SAVE_USER_INFO_SUCCESS:
                console.log(payload)
                break;
            case types.SAVE_USER_INFO_FAILED:
                draft.msg = errorMessage
                console.log(errorMessage)
                break;

            case types.SET_MSG:
                draft.msg = ''
                console.log(errorMessage)
                break;
            default:
                return state;
        }
    });
}
